import React, { useState, useEffect } from 'react'
import Tasks from './components/Tasks'
import TaskForm from './components/TaskForm'
import taskService from './services/tasks'


const App = () => {

    const [tasks, setTasks] = useState([])
    const [newTask, setNewTask] = useState('')

    useEffect(() => {
        console.log('Effect')
        taskService
            .getAll()
            .then(initialTasks => {
                setTasks(initialTasks)
            })
    }, [])

    const addTask = (event) => {
        event.preventDefault()

        const taskObject = {
            content: newTask
        } 

        taskService
            .create(taskObject)
            .then(returnedTask => {
                console.log('returnedTask', returnedTask)
                setTasks(tasks.concat(returnedTask))
                setNewTask('')
            })
    }

    const removeTask = (id) => {
        const taskFound = tasks.find(task => task.id === id)

        if (taskFound) {
            taskService
            .remove(id)
            .then(response => {
                setTasks(tasks.filter(task => task.id !== id))
            })
            .catch(error => {
                alert(
                    `The task '${taskFound.content}' was already deleted from server`
                )
                setTasks(tasks.filter(task => task.id !== id))
            })
        }
    }

    const switchStateOfTask = (id) => {
        const task = tasks.find(task => task.id === id) // find etsii ensimmäisen arvon listasta, joka vastaa käyttäjän klikkaamaa arvoa
        const changedTask = { ...task, done: !task.done } // kopioidaan klikkaama arvo ja asetetaan tilaksi vastakohta, koska klikkauksesta arvo muuttuu tehdyksi/ei tehdyksi

        taskService
            .update(id, changedTask)
            .then(returnedTask => {
                setTasks(tasks.map(task => task.id === id ? returnedTask : task))
        })
    }

    const editTask = () => {

    }

    const handleTaskChange = (event) => {
        console.log(event.target.value)
        setNewTask(event.target.value)
    }

    return (
        <div>
            <h1>Todo-list</h1>

            <TaskForm
                onSubmit={addTask}
                taskValue={newTask}
                onTaskChange={handleTaskChange}
            />
            <Tasks
                tasks={tasks}
                removeTask={removeTask}
                switchStateOfTask={switchStateOfTask}
            />

        </div>
    )
}

export default App
