import React from 'react'


const Tasks = (props) => {

    return (
        <div>
            <ul>
                {props.tasks.map(task => 
                // ruksimalla boksia (input onChange) käynnistetään funktio switchStateOfTask, 
                // jolle annetaan id (joka tulee ylläolevan mapin kautta)
                // checked ??
                // jos spanissa task.done on true (eli taski on tehty) -> silloin strikethrough -> tällöin className aktivoituu css:ssä,
                // jolloin yliviivataan taski. Jos false, silloin '' (tyhjä) 

                    <li key={task.id}>
                       <input onChange={() => props.switchStateOfTask(task.id)} type="checkbox" checked={task.done} />
                       <span className={task.done ? 'strikethrough' : ''}>{task.content}</span>
                       <button type="button" className="modify-button">Modify</button>
                       <button onClick={() => props.removeTask(task.id)} type="button" className="delete-button">X</button>
                    </li>
                )}
            </ul>
        </div>
    )
}

export default Tasks
