import Tasks from './components/Tasks'
import TaskForm from './components/TaskForm'
import personService from './services/tasks'


const App = () => {

    const [tasks, setTasks] = useState([])
    const [newTask, setNewTask] = useState('')

    useEffect(() => {
        console.log('Effect')
        personService
            .getAll()
            .then(initialTasks => {
                setTasks(initialTasks)
            })
    }, [])

    const addTask = (event) => {
        event.preventDefault()

        const taskObject = {
            task: newTask
        }

        personService.create(taskObject).then(returnedTask => {
            setTasks(tasks.concat(returnedTask))
            setNewTask('')
        })

    }

