import React from 'react'


const Tasks = (props) => {

    const change = task.done ? 'false' : 'true'

    return (
        <div>
            <ul>
                {props.tasks.map(task => 
                    <li key={task.id}>
                       <input onClick={change} className="strikethrough" type="checkbox" />
                       <span>{task.content}</span>
                       <button type="button" className="modify-button">Modify</button>
                       <button onClick={() => props.removeTask(task.id)} type="button" className="delete-button">X</button>
                    </li>
                )}
            </ul>
        </div>
    )
}

export default Tasks


